function filterBlacklisted($text)
{
    $blacklistedActivity = array("meetup", "hookup", "prostitute", "escort");
    $blacklistedPlatform = array("google", "gmail", "skype", "livejasmin", "chaturbate", "livu", "gmail", "google");
    $blacklistedPayment = array("bitcoin", "transferwise", "westernunion", "paypal", "payoneer", "skrill", "ethereum", "bitpay", "blockchain");
  
    $formatted = strtolower($text);
    $formatted = preg_replace("/@/", "a", $formatted);
    $formatted = trim(preg_replace("/[^a-zA-Z0-9\s]/", "", $formatted));
    $formattedList = array_filter(explode(" ", $formatted));
    $formattedJoined = join("", $formattedList);

    global $blacklistedActivities, $blacklistedPayments, $blacklistedPlatforms;

    foreach ($blacklistedActivity as $word) {
        if (preg_match("/$word/", $formattedJoined)) {
            return "activity forbidden";
        };
    };
    foreach ($blacklistedPayment as $word) {
        if (preg_match("/$word/", $formattedJoined)) {
            return "payment forbidden";
        };
    }
    foreach ($blacklistedPlatform as $word) {
        if (preg_match("/$word/", $formattedJoined)) {
            return "platform forbidden";
        };
    }
    foreach ($blacklistedActivities as $activity) {
        if (array_search($activity, $formattedList)) {
            return "activity forbidden";
        }
    }
    foreach ($blacklistedPayments as $payment) {
        if (array_search($payment, $formattedList)) {
            return "payment forbidden";
        }
    }
    foreach ($blacklistedPlatforms as $platform) {
        if (array_search($platform, $formattedList)) {
            return "platform forbidden";
        }
    }
    return "allowed";
}

function filterEmail($text)
{
    global $emailRegex;
    $normalized = preg_replace("/[^a-zA-Z0-9@.]/", '', normalize($text));
    if (preg_match($emailRegex, $normalized)) {
        return 'email forbidden';
    }
    return 'allowed';
}

function filterLink($text)
{
    global $linkRegex, $allowedLinksRegex;

    $normalized = preg_replace("/\s/", '', normalize($text));
    $splitted = explode(' ', $normalized);
    if (preg_match($linkRegex, $normalized)) {
        if (!preg_match($allowedLinksRegex, $normalized)) {
            return 'link forbidden';
        }
    }
    foreach ($splitted as $word) {
        if (preg_match($linkRegex, $word)) {
            if (!preg_match($allowedLinksRegex, $word)) {
                return 'link forbidden';
            }
        }
    }
    return 'allowed';
}

function filterForbidden($original)
{
    $text = removeEmoji($original);
    $resBlacklisted = filterBlacklisted($text);
    if ($resBlacklisted !== "allowed") {
        return $resBlacklisted;
    }
    $resEmail = filterEmail($text);
    if ($resEmail !== "allowed") {
        return $resEmail;
    }

    $resLink = filterLink($text);
    if ($resLink !== "allowed") {
        return $resLink;
    }
    return "allowed";
}

function normalize($text)
{
    return trim(strtolower($text));
}

function removeEmoji($text)
{
    global $emojiRegex;
    if (is_null($text)) {
        return null;
    }
    return preg_replace($emojiRegex, '', $text);
};

